mapper.update:
UPDATE User_Selection_Class_Map
SET isDeleted = 1
WHERE id = #{id}

mapper.select:
SELECT COUNT(1)
FROM User_Selection_Class_Map
WHERE userId = #{userId}
AND classId = #{classId}
AND isDeleted = 0

mapper.update:
UPDATE User_Selection_Class_Map
SET isPaid = 1, updateTime = {updateTime}
WHERE id = #{id}

UPDATE User_Selection_Class_Map
SET status = #{status}, isPaid = #{isPaid}
WHERE id = #{id}
AND isDeleted = 0

UPDATE User_Selection_Class_Map
SET isEvalueted = 1
WHERE id = #{id}
